import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{r,c,a as e,b as n,F as s,d as l,e as t,o as d}from"./app.af53d2d3.js";var p="/assets/d0c50-linux2bfile2bsystem2bhierarchy.9bf6a628.jpg";const a={},u=l('<h2 id="\u6811\u72B6\u76EE\u5F55\u7ED3\u6784" tabindex="-1"><a class="header-anchor" href="#\u6811\u72B6\u76EE\u5F55\u7ED3\u6784" aria-hidden="true">#</a> \u6811\u72B6\u76EE\u5F55\u7ED3\u6784</h2><p><img src="'+p+'" alt="img" loading="lazy"></p><h2 id="\u5177\u4F53\u76EE\u5F55\u89E3\u91CA" tabindex="-1"><a class="header-anchor" href="#\u5177\u4F53\u76EE\u5F55\u89E3\u91CA" aria-hidden="true">#</a> \u5177\u4F53\u76EE\u5F55\u89E3\u91CA</h2><ul><li><p><code>/bin</code> bin \u662F Binaries (\u4E8C\u8FDB\u5236\u6587\u4EF6) \u7684\u7F29\u5199\uFF0C\u8FD9\u4E2A\u76EE\u5F55\u5B58\u653E\u7740 <strong>\u6700\u7ECF\u5E38\u4F7F\u7528\u7684\u547D\u4EE4</strong>\u3002</p></li><li><p><code>/boot</code> \u8FD9\u91CC\u5B58\u653E\u7684\u662F\u542F\u52A8 Linux \u65F6\u4F7F\u7528\u7684\u4E00\u4E9B\u6838\u5FC3\u6587\u4EF6\uFF0C\u5305\u62EC\u4E00\u4E9B\u8FDE\u63A5\u6587\u4EF6\u4EE5\u53CA\u955C\u50CF\u6587\u4EF6\u3002</p></li><li><p><code>/dev</code> dev \u662F Device(\u8BBE\u5907) \u7684\u7F29\u5199\uFF0C \u8BE5\u76EE\u5F55\u4E0B\u5B58\u653E\u7684\u662F Linux \u7684\u5916\u90E8\u8BBE\u5907\uFF0C\u5728 Linux \u4E2D\u8BBF\u95EE\u8BBE\u5907\u7684\u65B9\u5F0F\u548C\u8BBF\u95EE\u6587\u4EF6\u7684\u65B9\u5F0F\u662F\u76F8\u540C\u7684\u3002</p></li><li><p><code>/etc</code> etc \u662F Etcetera(\u7B49\u7B49) \u7684\u7F29\u5199\uFF0C\u8FD9\u4E2A\u76EE\u5F55\u7528\u6765\u5B58\u653E\u6240\u6709\u7684 <strong>\u7CFB\u7EDF\u7BA1\u7406\u6240\u9700\u8981\u7684\u914D\u7F6E\u6587\u4EF6\u548C\u5B50\u76EE\u5F55</strong>\u3002</p></li><li><p><code>/home</code> \u7528\u6237\u7684\u4E3B\u76EE\u5F55\uFF0C\u5728 Linux \u4E2D\uFF0C\u6BCF\u4E2A\u7528\u6237\u90FD\u6709\u4E00\u4E2A\u81EA\u5DF1\u7684\u76EE\u5F55\uFF0C\u4E00\u822C\u8BE5\u76EE\u5F55\u540D\u662F\u4EE5\u7528\u6237\u7684\u8D26\u53F7\u547D\u540D\u7684\uFF0C\u5982\u4E0A\u56FE\u4E2D\u7684 alice\u3001bob \u548C eve\u3002</p></li><li><p><code>/lib</code> lib \u662F Library(\u5E93) \u7684\u7F29\u5199\u8FD9\u4E2A\u76EE\u5F55\u91CC\u5B58\u653E\u7740\u7CFB\u7EDF <strong>\u6700\u57FA\u672C\u7684\u52A8\u6001\u8FDE\u63A5\u5171\u4EAB\u5E93</strong>\uFF0C\u5176\u4F5C\u7528\u7C7B\u4F3C\u4E8E Windows \u91CC\u7684 DLL \u6587\u4EF6\u3002\u51E0\u4E4E\u6240\u6709\u7684\u5E94\u7528\u7A0B\u5E8F\u90FD\u9700\u8981\u7528\u5230\u8FD9\u4E9B\u5171\u4EAB\u5E93\u3002</p></li><li><p><code>/lost+found</code> \u8FD9\u4E2A\u76EE\u5F55\u4E00\u822C\u60C5\u51B5\u4E0B\u662F\u7A7A\u7684\uFF0C\u5F53\u7CFB\u7EDF\u975E\u6CD5\u5173\u673A\u540E\uFF0C\u8FD9\u91CC\u5C31\u5B58\u653E\u4E86\u4E00\u4E9B\u6587\u4EF6\u3002</p></li><li><p><code>/media</code> linux \u7CFB\u7EDF\u4F1A\u81EA\u52A8\u8BC6\u522B\u4E00\u4E9B\u8BBE\u5907\uFF0C\u4F8B\u5982U\u76D8\u3001\u5149\u9A71\u7B49\u7B49\uFF0C\u5F53\u8BC6\u522B\u540E\uFF0CLinux \u4F1A\u628A\u8BC6\u522B\u7684\u8BBE\u5907\u6302\u8F7D\u5230\u8FD9\u4E2A\u76EE\u5F55\u4E0B\u3002</p></li><li><p><code>/mnt</code> \u7CFB\u7EDF\u63D0\u4F9B\u8BE5\u76EE\u5F55\u662F\u4E3A\u4E86\u8BA9\u7528\u6237\u4E34\u65F6\u6302\u8F7D\u522B\u7684\u6587\u4EF6\u7CFB\u7EDF\u7684\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5C06\u5149\u9A71\u6302\u8F7D\u5728 /mnt/ \u4E0A\uFF0C\u7136\u540E\u8FDB\u5165\u8BE5\u76EE\u5F55\u5C31\u53EF\u4EE5\u67E5\u770B\u5149\u9A71\u91CC\u7684\u5185\u5BB9\u4E86\u3002</p></li><li><p><code>/opt</code> opt \u662F optional(\u53EF\u9009) \u7684\u7F29\u5199\uFF0C\u8FD9\u662F\u7ED9\u4E3B\u673A <strong>\u989D\u5916\u5B89\u88C5\u8F6F\u4EF6\u6240\u6446\u653E\u7684\u76EE\u5F55</strong>\u3002\u6BD4\u5982\u4F60\u5B89\u88C5\u4E00\u4E2AORACLE\u6570\u636E\u5E93\u5219\u5C31\u53EF\u4EE5\u653E\u5230\u8FD9\u4E2A\u76EE\u5F55\u4E0B\u3002\u9ED8\u8BA4\u662F\u7A7A\u7684\u3002</p></li><li><p><code>/proc</code> proc \u662F Processes(\u8FDB\u7A0B) \u7684\u7F29\u5199\uFF0C/proc \u662F\u4E00\u79CD\u4F2A\u6587\u4EF6\u7CFB\u7EDF\uFF08\u4E5F\u5373\u865A\u62DF\u6587\u4EF6\u7CFB\u7EDF\uFF09\uFF0C\u5B58\u50A8\u7684\u662F\u5F53\u524D\u5185\u6838\u8FD0\u884C\u72B6\u6001\u7684\u4E00\u7CFB\u5217\u7279\u6B8A\u6587\u4EF6\uFF0C\u8FD9\u4E2A\u76EE\u5F55\u662F\u4E00\u4E2A\u865A\u62DF\u7684\u76EE\u5F55\uFF0C\u5B83\u662F\u7CFB\u7EDF\u5185\u5B58\u7684\u6620\u5C04\uFF0C\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u76F4\u63A5\u8BBF\u95EE\u8FD9\u4E2A\u76EE\u5F55\u6765\u83B7\u53D6\u7CFB\u7EDF\u4FE1\u606F\u3002</p></li><li><p><code>/root</code> \u8BE5\u76EE\u5F55\u4E3A\u7CFB\u7EDF\u7BA1\u7406\u5458\uFF0C\u4E5F\u79F0\u4F5C\u8D85\u7EA7\u6743\u9650\u8005\u7684\u7528\u6237\u4E3B\u76EE\u5F55\u3002</p></li><li><p><code>/sbin</code> s \u5C31\u662F Super User \u7684\u610F\u601D\uFF0C\u662F Superuser Binaries (\u8D85\u7EA7\u7528\u6237\u7684\u4E8C\u8FDB\u5236\u6587\u4EF6) \u7684\u7F29\u5199\uFF0C\u8FD9\u91CC\u5B58\u653E\u7684\u662F\u7CFB\u7EDF\u7BA1\u7406\u5458\u4F7F\u7528\u7684\u7CFB\u7EDF\u7BA1\u7406\u7A0B\u5E8F\u3002</p></li><li><p><code>/selinux</code> \u8FD9\u4E2A\u76EE\u5F55\u662F Redhat/CentOS \u6240\u7279\u6709\u7684\u76EE\u5F55\uFF0CSelinux \u662F\u4E00\u4E2A\u5B89\u5168\u673A\u5236\uFF0C\u7C7B\u4F3C\u4E8E windows \u7684\u9632\u706B\u5899\uFF0C\u4F46\u662F\u8FD9\u5957\u673A\u5236\u6BD4\u8F83\u590D\u6742\uFF0C\u8FD9\u4E2A\u76EE\u5F55\u5C31\u662F\u5B58\u653E selinux \u76F8\u5173\u7684\u6587\u4EF6\u7684\u3002</p></li><li><p><code>/srv</code> \u8BE5\u76EE\u5F55\u5B58\u653E\u4E00\u4E9B\u670D\u52A1\u542F\u52A8\u4E4B\u540E\u9700\u8981\u63D0\u53D6\u7684\u6570\u636E\u3002</p></li><li><p><code>/sys</code></p><p>\u8FD9\u662F Linux2.6 \u5185\u6838\u7684\u4E00\u4E2A\u5F88\u5927\u7684\u53D8\u5316\u3002\u8BE5\u76EE\u5F55\u4E0B\u5B89\u88C5\u4E86 2.6 \u5185\u6838\u4E2D\u65B0\u51FA\u73B0\u7684\u4E00\u4E2A\u6587\u4EF6\u7CFB\u7EDF sysfs \u3002</p><p>sysfs \u6587\u4EF6\u7CFB\u7EDF\u96C6\u6210\u4E86\u4E0B\u9762 3 \u79CD\u6587\u4EF6\u7CFB\u7EDF\u7684\u4FE1\u606F\uFF1A\u9488\u5BF9\u8FDB\u7A0B\u4FE1\u606F\u7684 proc \u6587\u4EF6\u7CFB\u7EDF\u3001\u9488\u5BF9\u8BBE\u5907\u7684 devfs \u6587\u4EF6\u7CFB\u7EDF\u4EE5\u53CA\u9488\u5BF9\u4F2A\u7EC8\u7AEF\u7684 devpts \u6587\u4EF6\u7CFB\u7EDF\u3002</p><p>\u8BE5\u6587\u4EF6\u7CFB\u7EDF\u662F\u5185\u6838\u8BBE\u5907\u6811\u7684\u4E00\u4E2A\u76F4\u89C2\u53CD\u6620\u3002</p><p>\u5F53\u4E00\u4E2A\u5185\u6838\u5BF9\u8C61\u88AB\u521B\u5EFA\u7684\u65F6\u5019\uFF0C\u5BF9\u5E94\u7684\u6587\u4EF6\u548C\u76EE\u5F55\u4E5F\u5728\u5185\u6838\u5BF9\u8C61\u5B50\u7CFB\u7EDF\u4E2D\u88AB\u521B\u5EFA\u3002</p></li><li><p><code>/tmp</code> tmp \u662F temporary(\u4E34\u65F6) \u7684\u7F29\u5199\u8FD9\u4E2A\u76EE\u5F55\u662F\u7528\u6765\u5B58\u653E\u4E00\u4E9B\u4E34\u65F6\u6587\u4EF6\u7684\u3002</p></li><li><p><code>/usr</code> usr \u662F unix shared resources(\u5171\u4EAB\u8D44\u6E90) \u7684\u7F29\u5199\uFF0C\u8FD9\u662F\u4E00\u4E2A\u975E\u5E38\u91CD\u8981\u7684\u76EE\u5F55\uFF0C\u7528\u6237\u7684\u5F88\u591A\u5E94\u7528\u7A0B\u5E8F\u548C\u6587\u4EF6\u90FD\u653E\u5728\u8FD9\u4E2A\u76EE\u5F55\u4E0B\uFF0C\u7C7B\u4F3C\u4E8E windows \u4E0B\u7684 program files \u76EE\u5F55\u3002</p></li><li><p><code>/usr/bin</code> <strong>\u7CFB\u7EDF\u7528\u6237\u4F7F\u7528\u7684\u5E94\u7528\u7A0B\u5E8F</strong>\u3002</p></li><li><p><code>/usr/sbin</code> \u8D85\u7EA7\u7528\u6237\u4F7F\u7528\u7684\u6BD4\u8F83\u9AD8\u7EA7\u7684\u7BA1\u7406\u7A0B\u5E8F\u548C\u7CFB\u7EDF\u5B88\u62A4\u7A0B\u5E8F\u3002</p></li><li><p><code>/usr/src</code> <strong>\u5185\u6838\u6E90\u4EE3\u7801\u9ED8\u8BA4\u7684\u653E\u7F6E\u76EE\u5F55</strong>\u3002</p></li><li><p><code>/var</code> var \u662F variable(\u53D8\u91CF) \u7684\u7F29\u5199\uFF0C\u8FD9\u4E2A\u76EE\u5F55\u4E2D\u5B58\u653E\u7740\u5728\u4E0D\u65AD\u6269\u5145\u7740\u7684\u4E1C\u897F\uFF0C\u6211\u4EEC\u4E60\u60EF\u5C06\u90A3\u4E9B\u7ECF\u5E38\u88AB\u4FEE\u6539\u7684\u76EE\u5F55\u653E\u5728\u8FD9\u4E2A\u76EE\u5F55\u4E0B\u3002\u5305\u62EC <strong>\u5404\u79CD\u65E5\u5FD7\u6587\u4EF6</strong>\u3002</p></li><li><p><code>/run</code> \u662F\u4E00\u4E2A\u4E34\u65F6\u6587\u4EF6\u7CFB\u7EDF\uFF0C\u5B58\u50A8\u7CFB\u7EDF\u542F\u52A8\u4EE5\u6765\u7684\u4FE1\u606F\u3002\u5F53\u7CFB\u7EDF\u91CD\u542F\u65F6\uFF0C\u8FD9\u4E2A\u76EE\u5F55\u4E0B\u7684\u6587\u4EF6\u5E94\u8BE5\u88AB\u5220\u6389\u6216\u6E05\u9664\u3002\u5982\u679C\u4F60\u7684\u7CFB\u7EDF\u4E0A\u6709 /var/run \u76EE\u5F55\uFF0C\u5E94\u8BE5\u8BA9\u5B83\u6307\u5411 run\u3002</p></li></ul><h2 id="\u8F6C\u8F7D" tabindex="-1"><a class="header-anchor" href="#\u8F6C\u8F7D" aria-hidden="true">#</a> \u8F6C\u8F7D</h2>',5),h={href:"https://www.runoob.com/linux/linux-system-contents.html",target:"_blank",rel:"noopener noreferrer"},m=t("Linux \u7CFB\u7EDF\u76EE\u5F55\u7ED3\u6784 | \u83DC\u9E1F\u6559\u7A0B (runoob.com)");function x(b,_){const o=r("ExternalLinkIcon");return d(),c(s,null,[u,e("ul",null,[e("li",null,[e("a",h,[m,n(o)])])])],64)}var v=i(a,[["render",x],["__file","contents.html.vue"]]);export{v as default};
